import { OnInit, Component } from '@angular/core';
import { TranslateService } from '@ngx-translate/core';
import { NbSidebarService } from '@nebular/theme';

import { NbMenuItem } from '@nebular/theme';
import { group } from '@angular/animations';
import { Router, NavigationEnd } from '@angular/router';

export const MENU_ITEMS: NbMenuItem[] = [{
    title: "Menú Principal",
    group: true
  },{
    title: "Cursos",
    link: "/cursos",
    icon: "book-open-outline"
  },{
    title: "Reportes",
    link: "",
    icon: "file-text-outline",
    children: [{
      title: "Reporte Pie",
      link: "/reportes/pie"
    },{
      title: "Reporte Barras",
      link: "/reportes/bar"
    }]
  },{
    title: "Analiticas",
    icon: "bar-chart-outline",
    children: [{
      title: "Gráfica de Barras",
      link: "/analiticas/grafica_barras"
    },{
      title: "Gráfica de Barras Horizontal",
      link: "/analiticas/grafica_barrash"
    },{
      title: "Gráfica de Pie",
      link: "/analiticas/grafica_pie"
    },]
  },{
    title: "Usuarios",
    link: "/usuarios",
    icon: "person-done-outline"
  }, {
    title: "Bug Tracking",
    link: "/tracking",
    icon: "eye-outline"
  }, {
    title: "Gamification",
    link: "/gamification",
    icon: "gift-outline"
  },
];

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  title = 'eTrainersSystem';
  // fawesome icon init
  rutaActual: string = "";
  navegacion:string[]=[];

  menu = MENU_ITEMS;
  currentUser = "User 1";
  userPictureOnly: boolean = false;
  user: object = {
    name: "Jhon Smith",
    picture: "data:image/jpeg;base64,"
  };

  themes: string[] = ["Theme 1", "Theme 2", "Theme 3", "Theme 4"];

  userMenu = [{ title: 'Profile' }, { title: 'Log out' }];

  constructor(private _router: Router, private translate: TranslateService, private _sidebar: NbSidebarService) {
    // this language will be used as a fallback when a translation isn't found in the current language
    translate.setDefaultLang('es');

    // the lang to use, if the lang isn't available, it will use the current loader to get them
    translate.use('es');
  }

  ngOnInit() {
    this._router.events.subscribe(value => {
      this.rutaActual = this._router.url.toString();
      if (value instanceof NavigationEnd) {
        this.actualizarRuta(this._router.url.toString().split("/"));
      }
    });
  }

  actualizarRuta(newUrl: string[]) {
    if (!newUrl) {
      return;
    }

    if (newUrl.length == 2 && newUrl[1] == "") {
      newUrl = [];
    } else {
      newUrl.shift();
    }

    newUrl = this.renombrarUrl(newUrl);

    this.navegacion = newUrl;
  }

  renombrarUrl(urlArray:string[]): string[]{

    let nombres = [{
      ruta: "grafica_barras",
      nombre: "Grafica de Barras"
    },{
      ruta: "grafica_barrash",
      nombre: "Grafica de Barras Horizontal"
    },{
      ruta: "grafica_pie",
      nombre: "Grafica tipo Pay"
    }]

    for (let i = 0; i < urlArray.length; i++) {
        for (let j = 0; j < nombres.length; j++) {
          if(urlArray[i] === nombres[j].ruta){
            urlArray[i] = nombres[j].nombre;
          }
        }
    }

    return urlArray;
  }

  toggleSidebar(){
    this._sidebar.toggle();
  }
}
